document.addEventListener('DOMContentLoaded', function() {
    const departmentSelect = document.getElementById('departmentSelect1');
    const coursesSelect = document.getElementById('coursesSelect1');

    // Department-Course mappings
    const coursesOptions = {
        1: ['Agricultural Engineering', 'Animal Science', 'Crop Science', 'Food Science and Technology'],
        2: ['Business Administration', 'Accounting', 'Public Administration', 'Purchasing and Supply', 'Tourism and Hospitality Management', 'Marketing', 'Banking and Finance'],
        3: ['Educational Psychology', 'Guidance and Counselling', 'Adult Education', 'Curriculum Studies', 'Educational Management', 'Educational Technology', 'Science Education', 'Social Science Education', 'Language Education', 'Physical and Health Education', 'Vocational and Technical Education', 'Special Education', 'Early Childhood Education', 'Library and Information Science'],
        4: ['Architecture and interior design', 'Building Technology', 'Quantity Surveying', 'Urban and Regional Planning', 'Surveying and Geoinformatics', 'Textile Design and Technology'],
        5: ['Human Anatomy', 'Human Physiology', 'Medical Biochemistry', 'Medical Laboratory Science', 'Nursing Science', 'Physiotherapy', 'Radiography', 'Human Nutrition and Dietetics'],
        6: ['Civil Law', 'Common Law', 'International Law', 'Private Law', 'Public Law', 'Islamic Law', 'Law'],
        7: ['Microbiology and Biotechnology', 'Chemistry', 'Mathematics & Actuarial Science', 'Plant Sciences', 'Computing & Information Science', 'Physics']
    };

    departmentSelect.addEventListener('change', function() {
        const selectedDepartment = this.value;
        
        // Clear existing options
        coursesSelect.innerHTML = '<option value="" disabled selected>Courses</option>';

        // Try API first, fallback to local options
        fetch(`/api/courses/${selectedDepartment}/`)
            .then(response => {
                if (!response.ok) throw new Error('API fetch failed');
                return response.json();
            })
            .then(courses => {
                courses.forEach(course => {
                    const option = document.createElement('option');
                    option.value = course.id;
                    option.textContent = course.name;
                    coursesSelect.appendChild(option);
                });
            })
            .catch(() => {
                // Fallback to local options
                const localCourses = coursesOptions[selectedDepartment] || [];
                localCourses.forEach(course => {
                    const option = document.createElement('option');
                    option.value = course;
                    option.textContent = course;
                    coursesSelect.appendChild(option);
                });
            });
    });
});
