document.addEventListener('DOMContentLoaded', function() {
    const departmentSelect = document.getElementById('departmentSelect1');
    const coursesSelect = document.getElementById('coursesSelect1');
    const currentCourse = coursesSelect.value;

    const coursesOptions = {
        1: ['Agricultural Economics','Animal Science',' Agricultural Sciences and Technology','Crop Science','Food Science and Technology','Forestry and Wildlife','Horticulture','Soil Science','Water Resources Management and Agro-meteorology'],
        2: ['Business Administration','Accounting','Public Administration','Purchasing and Supply','Tourism and Hospitality Management,','Marketing','Banking and Finance','Tourism and Hospitality Management,'],
        3: ['Educational Psychology','Guidance and Counselling','Adult Education','Curriculum Studies','Educational Management','Educational Technology','Science Education','Social Science Education','Language Education','Physical and Health Education','Vocational and Technical Education','Special Education','Early Childhood Education','Educational Psychology','Guidance and Counselling','Curriculum Studies','Educational Management','Science Education','Social Science Education','Language Education','Physical and Health Education','Vocational and Technical Education','Special Education','Early Childhood Education','Library and Information Science'],
        4: [' Architecture and interior design', 'Building Technology', 'Quantity Surveying', 'Urban and Regional Planning', 'Surveying and Geoinformatics', 'Textile Design and Technology'],
        5: ['Human Anatomy', 'Human Physiology', 'Medical Biochemistry', 'Medical Laboratory Science', 'Nursing Science', 'Physiotherapy', 'Radiography', 'Radiography', 'Human Nutrition and Dietetics'],
        6: ['Civil Law', 'Common Law', 'International Law', 'Private Law', 'Public Law', 'Islamic Law', 'Law', 'Civil Law', 'Common Law', 'International Law', 'Private Law', 'Public Law', 'Islamic Law'],
        7: ['Microbiology and Biotechnology','Chemistry','Mathematics & Actuarial Science','Plant Sciences','Computing & Information Science','Physics']
    };

    function loadCourses(department, selectedCourse = null) {
        coursesSelect.innerHTML = '<option value="" disabled selected>Courses</option>';
        const courses = coursesOptions[department] || [];
        
        courses.forEach(course => {
            const option = document.createElement('option');
            option.value = course;
            option.textContent = course;
            if (selectedCourse && course === selectedCourse) {
                option.selected = true;
            }
            coursesSelect.appendChild(option);
        });
    }

    // Load initial courses for pre-selected department
    if (departmentSelect.value) {
        loadCourses(departmentSelect.value, currentCourse);
    }

    departmentSelect.addEventListener('change', function() {
        const department = this.value;
        loadCourses(department);
    });
});
